mapper.update:
UPDATE gym_user
SET name = #{name}, email_address = #{emailAddress}, membership_fee = #{membershipFee}, admission_day = #{admissionDay}
WHERE id = #{id}

UPDATE  tickets
SET user_id = #{userId}, remaining = #{remaining}, buy_day = #{buyDay}, user_name = #{userName},  ticket_name = #{ticketName}
WHERE ticket_number = #{ticketNumber}
